import React from 'react';

const Button = (props) => {
  return (
    <button className={props.className} onClick={props.handleLike}>

    </button>
  );
}

export default class LikeDislike extends React.Component {
  state = {
    likes: 100,
    dislikes: 25
  }

  handleLike() {
    this.setState({
      likes: this.state.likes+1
    });
  }

  handleDislike() {
    this.setState({
      dislikes: this.state.dislikes+1
    });
  }

  render () {
    return (
      <React.Fragment>
        <div className="App">
          <h2>Like/Dislike</h2>
          <Button className="like-button" handleLike={() => this.handleLike()}>
            Likes | <span>{this.state.likes}</span>
          </Button>
          <Button className="dislike-button" handleLike={() => this.handleDislike()}>
            Dislikes | <span>{this.state.dislikes}</span>
          </Button>
        </div>
        <style>
          {`
            .like-button, .dislike-button {
              display: inline-block;
              border: 1px solid #000;
            }

            .liked, disliked {
              color: blue;
              border: 1px solid blue;
            }

            .App {
              margin: 20px;
            }
          `}
        </style>
      </React.Fragment>
    )
  }
}
