import React from 'react';

const Button = (props) => {
  return (
    <button className={props.className} onClick={props.handleLike}>
      {props.children}
    </button>
  );
}

export default class LikeDislike extends React.Component {
  state = {
    likes: 100,
    liked: false,
    dislikes: 25,
    disliked: false
  }

  handleLike() {
    const likes = (!this.state.liked) ? this.state.likes+1 : this.state.likes-1;
    this.setState({
      likes: likes,
      liked: !this.state.liked
    });

    if(this.state.disliked) handleDislike();
  }

  handleDislike() {
    const dislikes = (!this.state.disliked) ? this.state.dislikes+1 : this.state.dislikes-1;
    this.setState({
      dislikes: dislikes,
      disliked: !this.state.disliked
    });
  }

  render () {
    const liked = this.state.liked ? 'liked' : '';
    const disliked = this.state.disliked ? 'disliked' : '';

    return (
      <React.Fragment>
        <div className="App">
          <h2>Like/Dislike</h2>
          <Button className={`like-button ${liked}`} handleLike={() => this.handleLike()}>
            Likes | <span>{this.state.likes}</span>
          </Button>
          <Button className={`dislike-button ${disliked}`} handleLike={() => this.handleDislike()}>
            Dislikes | <span>{this.state.dislikes}</span>
          </Button>
        </div>
        <style>
          {`
            .like-button, .dislike-button {
              display: inline-block;
              border: 1px solid #000;
            }

            .liked, .disliked {
              color: blue;
              border: 1px solid blue;
            }

            .App {
              margin: 20px;
            }
          `}
        </style>
      </React.Fragment>
    )
  }
}
